import bodyParser from 'body-parser';
import compress from 'compression';
import cors from 'cors';
import express from 'express';
import favicon from 'favicon';
import qs from 'qs';
import nats from 'nats';
import mostly from 'mostly-node';
import poplar from 'mostly-poplarjs-rest';
import feathers from 'mostly-feathers-rest';

import { config } from 'common';
import errorHandler from './error_handler';

const trans = new mostly(nats.connect(config.nats), {
  logLevel: 'info'
});

const app = express();

// overrite the express/qs parser's depth limit
app.set('query parser', function (str) {
  return qs.parse(str, { depth: 10 });
});

app.use(compress())
  .options('*', cors({ maxAge: 60 * 60 * 24 * 365 }))
  .use(cors())
  .use(bodyParser.json())
  .use(bodyParser.urlencoded({ extended: true }));

trans.ready(() => {
  app.use(poplar(app, trans, '/poplar/v1', { version: 'v1' }));
  app.use(poplar(app, trans, '/poplar/v1.1', { version: 'v1.1' }));
  app.use(feathers(app, trans, '/feathers'));
  app.use(errorHandler());
  app.listen(process.env.PORT || 4001);
});

trans.onError(e => {
  console.error('Error [' + config.nats + ']: ' + e);
  process.exit();
});
