const bodyParser = require('body-parser');
const compress = require('compression');
const cors = require('cors');
const express = require('express');
const favicon = require('favicon');
const qs = require('qs');
const nats = require('nats');
const mostly = require('mostly-node');
const poplar = require('mostly-poplarjs-rest');
const feathers = require('mostly-feathers-rest');

const { config } = require('common');
const errorHandler = require('./error_handler');

const trans = new mostly(nats.connect(config.nats), {
  logLevel: 'info'
});

const app = express();

// overrite the express/qs parser's depth limit
app.set('query parser', function (str) {
  return qs.parse(str, { depth: 10 });
});

app.use(compress())
  .options('*', cors({ maxAge: 60 * 60 * 24 * 365 }))
  .use(cors())
  .use(bodyParser.json())
  .use(bodyParser.urlencoded({ extended: true }));

trans.ready(() => {
  app.use(poplar(app, trans, '/poplar/v1', { version: 'v1' }));
  app.use(poplar(app, trans, '/poplar/v1.1', { version: 'v1.1' }));
  app.use(feathers(app, trans, '/feathers'));
  app.use(errorHandler());
  app.listen(process.env.PORT || 4001);
});

trans.onError(e => {
  console.error('Error [' + config.nats + ']: ' + e);
  process.exit();
});
