import { upperFirst, camelCase } from 'lodash';
import path from 'path';
import mongoose from 'mongoose';

let models = {};
let entities = {};

// Load All Models
[
  'dummy'
].forEach(function(file) {
  const name = upperFirst(camelCase(file));
  models[name] = require(path.join(__dirname, file, 'model'));
  entities[name] = require(path.join(__dirname, file, 'entity'));
});

export { models as default, entities };