import { hooks } from 'mostly-feathers-mongoose';

import DummyEntity from '../../entities/dummy.entity';

export default function (options = {}) {
  return {
    before: {
      all: [
        hook => console.log('before all hook ran')
      ],
      find: [],
      get: [],
      create: [],
      update: [],
      patch: [],
      remove: []
    },
    after: {
      all: [
        hook => console.log('after all hook ran'),
        hooks.presentEntity(DummyEntity),
        hooks.responder()
      ],
      find: [],
      get: [],
      create: [],
      update: [],
      patch: [],
      remove: []
    }
  };
}
