const { hooks } = require('mostly-feathers-mongoose');

const DummyEntity = require('../../entities/dummy.entity');

module.exports = function (options = {}) {
  return {
    before: {
      all: [
        hook => console.log('before all hook ran')
      ],
      find: [],
      get: [],
      create: [],
      update: [],
      patch: [],
      remove: []
    },
    after: {
      all: [
        hook => console.log('after all hook ran'),
        hooks.presentEntity(DummyEntity),
        hooks.responder()
      ],
      find: [],
      get: [],
      create: [],
      update: [],
      patch: [],
      remove: []
    }
  };
};