const { Service, createService } = require('mostly-feathers-mongoose');

const DummyModel = require('../../models/dummy.model');
const defaultHooks = require('./dummy.hooks');

export class DummyService extends Service {
  constructor(options) {
    super(options);
  }

  setup (app) {
    super.setup(app);
    this.hooks(defaultHooks(this.options));
  }

  testAction (id, data, params) {
    data.destroyedAt = new Date();
    return super.patch(id, data, params);
  }
}

module.exports = function init (app, options, hooks) {
  options = Object.assign({ ModelName: 'dummy' }, options);
  return createService(app, DummyService, DummyModel, options);
};
module.exports.Service = DummyService;