import { Service, createService } from 'mostly-feathers-mongoose';

import DummyModel from '../../models/dummy.model';
import defaultHooks from './dummy.hooks';

export class DummyService extends Service {
  constructor(options) {
    super(options);
  }

  setup (app) {
    super.setup(app);
    this.hooks(defaultHooks(this.options));
  }

  testAction (id, data, params) {
    data.destroyedAt = new Date();
    return super.patch(id, data, params);
  }
}

export default function init (app, options, hooks) {
  options = Object.assign({ ModelName: 'dummy' }, options);
  return createService(app, DummyService, DummyModel, options);
}

init.Service = DummyService;