import { Service as BaseService } from 'mostly-feathers-mongoose';
import { config } from 'common';
import models from '../../models';

export default class DummyService extends BaseService {
  constructor() {
    super({
      name: 'DummyService',
      Model: models.Dummy,
      lean: true,
      paginate: config.mongoose.paginate
    });
  }
  
  testAction (id, data, params) {
    data.destroyedAt = new Date();
    return super.patch(id, data, params);
  }
}
