import hooks from './hooks';
import memory from 'feathers-memory';

export default function() {
  const app = this;

  // initialize service
  const service = new memory();
  app.use('memory', service);

  // service hooks
  app.service('memory').hooks(hooks());

  // some testing data
  const memories = app.service('memory');

  memories.create({id: 1, text: 'A million people walk into a Silicon Valley bar'}, {}, function(){});
  memories.create({id: 2, text: 'Nobody buys anything'}, {}, function(){});
  memories.create({id: 3, text: 'Bar declared massive success'}, {}, function(){});
  memories.create({id: 4, text: 'Foo meet memories'}, {}, function(){});
}
